-- ----------------------------
-- Table structure for bg_artice
-- ----------------------------
DROP TABLE IF EXISTS `bg_artice`;
CREATE TABLE `bg_artice` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(100) CHARACTER SET utf8 DEFAULT NULL,
  `author` varchar(60) CHARACTER SET utf8 DEFAULT NULL,
  `pic` varchar(255) CHARACTER SET utf8 DEFAULT NULL,
  `brief` varchar(255) CHARACTER SET utf8 DEFAULT NULL COMMENT '文章简介',
  `content` text CHARACTER SET utf8,
  `keywords` varchar(60) CHARACTER SET utf8 DEFAULT NULL,
  `des` varchar(255) CHARACTER SET utf8 DEFAULT NULL COMMENT '描述',
  `rem` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否推荐',
  `cateid` mediumint(6) DEFAULT NULL,
  `click` int(11) NOT NULL DEFAULT '0' COMMENT '点击数',
  `likes` mediumint(6) NOT NULL DEFAULT '0' COMMENT '点赞数',
  `time` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=32 DEFAULT CHARSET=latin1;

